import { useState } from "react"
import "./AddTaskStyle.css";

const AddTask=()=>{

    const[inputtxt,setInputtxt] = useState("");

    const sendToServer = ()=>{
        const payload = {
            "text":inputtxt,
        }
        const fetchopt = {
            method:"POST",
            body:JSON.stringify(payload),
            headers: {
                "Content-Type": "application/json"
            }
        }
        fetch("http://localhost:3000/todos/add/",fetchopt)
        .then(res=>res.json())
        .then(data=>console.log(data))
        .catch(err=>console.log(err))
    }

    const updatetxt =(e)=>{
        setInputtxt(e.target.value);
        console.log("value of txt "+inputtxt)
    }
    return(
        <div>
            <body style={{background: "#A0D8B3"}}>
            <h1 style={{color:"#83764F",fontSize:"3em",fontFamily:"Lucida Sans"}}>Add New Task In Your Todo</h1>
            <form  onSubmit={sendToServer} action="http://localhost:3001">
                <input type="text" placeholder="+Task" onChange={updatetxt}/>
                <input type="submit" value="SUBMIT" />
            </form>
            </body>
        </div>
    )
}

export default AddTask;
